import React, {Component} from 'react';

class Form extends Component {


state ={
    user_id:'',
    password:'',
    list:[],
};

handelChange= (e) =>{
		const { name, value } = e.target;
		this.setState({
			[name]: value,
		});
};

handelSubmit= (e) =>{

    e.preventDefault();
    const { list, user_id, password } = this.state;
    this.setState({
        user_id:'',
        password:'',
        list: list.concat({
           user_id,
           password, 
        }),
        id: null,
    });

    this.props.onInsert(this.state.user_id, this.state.password);

};
handleFocus=()=>{
this.props.onFocus(this.state.id);
}

    render(){



        return(
            <div>
                <form onSubmit={this.handelSubmit} > 
                    <input name="user_id"
                        value={this.state.user_id}
                        onChange={this.handelChange}
                        onFocus={this.handleFocus}

                    />
                    <br/>
                    <input name="password"
                        value={this.state.password}
                        onChange={this.handelChange}
                    />
                    <br/>
                    <button>제출</button>
                </form>
            </div>
        )
    }
}

export default Form;
