import React, {Component} from 'react';

class Form extends Component {
state ={
    text:'',
};

handelChange= (e) =>{
    alert("111");
		const { name, value } = e.target;
		this.setState({
			[name]: value,
		});
};

    render(){


        return(
            <div>
                    <input name="user_id"
                        value={this.state.text}
                        onChange={this.handelChange}

                    />
                    <br/>
                    <input name="password"
                        value={this.state.text}
                        onChange={this.handelChange}
                    />
                    <br/>
                    <button></button>
            </div>
        )
    }
}

export default Form;
