import React, {Component} from 'react';
import './Item.css';

class Item extends Component{
	shouldComponentUpdate(nextProps, nextState){
		if(this.props.todo !== nextProps.todo){
			return true;
        }else{
            if(this.props.todo !== nextProps.todo){
                return true;
            }
            return false;
        }
    }

    state={
        user_id:'',
        password:'',
    }

    render(){
        const {todo, onUpdate } = this.props;
        return(
            <div>
                <div>{todo.user_id}와 {todo.password}입니다.</div>
                <div className={`update_from ${!todo.done && 'hide'}`}>
                    <input value={todo.user_id} />
                    <br/>
                    <input value={todo.password} />
                </div>
                <button onClick={ ()=> onUpdate(todo.id)}>수정하기</button>
                <br/>
            </div>

        )
    }
}

export default Item;
