import React, {Component} from 'react';
import Item from './Item';

class List extends Component {
    handleChange= ()=> {
        alert("2222");
    }
    render(){
        const{todos, onUpdate} =this.props;
        return(
            <div onChange={this.handleChange}>{
                todos.map((todo) =>{
                    return <Item key={todo.id} todo={todo} onUpdate={onUpdate} /> 
                })}
            </div>
        )
    }
}

export default List;
