import React, { Component } from 'react';
import './App.css';
import Form from './components/Form';
import List from './components/List';

class App extends Component {
id=1;
state={
  todos:[],
};

handleInsert=(e, i)=>{

  this.setState({
    todos: this.state.todos.concat({
      user_id: e,
      password: i,
      id: this.id,
      done: false,
    }),

  });
  this.id++;
  if(this.id !== 0){
      this.handleBack();
  }
};

handleUpdate=(id)=>{

  this.setState({
    todos: this.state.todos.map((todo)=>{
      if(todo.id === id){

        return{
          ...todo,
          done:!todo.done,
        }
      }else {
        return{
          ...todo,
          done: false,
        }
      }

    })
  });

};


handleBack= ()=>{


  this.setState({
    todos:this.state.todos.map(todo => todo.done === true ? {...todo, done:false} : todo)
  })


};
  render(){
      return(
        <div className='App'>
          <Form onInsert={this.handleInsert}
          />
          <List 
            todos={this.state.todos}
            onUpdate={this.handleUpdate}
          />
        </div>
      )
  }

}

export default App;
