import React, { Component } from 'react';
import './App.css';
import Form from './components/Form';
import List from './components/List';

class App extends Component {
id=1;
state={
  todos:[],
};

handleInsert=(e, i)=>{
  this.handleUpdate();
  this.setState({
    todos: this.state.todos.concat({
      user_id:e,
      password: i,
      id: this.id,
      done: false,
    }),

  });
  this.id++;

};

handleUpdate=(id)=>{
if(id !== undefined){
  this.setState({
    todos: this.state.todos.map((todo)=>{
      if(todo.id === id){

        return{
          ...todo,
          done:!todo.done,
        }
      }else {
        return{
          ...todo,
          done: false,
        }
      }

    })
  });
}else{
  this.setState({
    todos: this.state.todos.map((todo)=>{
      if(todo.id === id){

        return{
          ...todo,
          done:false,
        }
      }else {
        return{
          ...todo,
          done: false,
        }
      }

    })
  });
}

};



  render(){
      return(
        <div className='App'>
          <Form onInsert={this.handleInsert}
          />
          <List 
            todos={this.state.todos}
            onUpdate={this.handleUpdate}
          />
        </div>
      )
  }

}

export default App;
